import React, { useEffect, useRef } from 'react'
import { useDeviceDetect } from '../../../Helpers'

export const FlipBook = ({ className, pages }) => {
  let fadeClass = useRef(null)
  const { mobile, tablet, laptop, desktop } = useDeviceDetect()
  
  useEffect(() => {
    if (fadeClass) {
      $(fadeClass).turn({
        width: mobile ? 340 : (laptop || desktop) ? 800 : tablet ? 690 : 1100,
        height: mobile ? 250 : (laptop || desktop) ? 350 : tablet ? 250 : 400,
        autoCenter: true,
        display: window.innerWidth < 577 ? 'single' : 'double',
        duration: 1200,
        turnCorners: "bl, br"
      })
    }
  }, [])
  
  return (
    <div className={`flipbook ${className ? className : ''}`} ref={el => fadeClass = el}>
      {pages && pages.map((page, index) => (
        <div key={index} className="page">
          <img src={page} alt="Brandbook page"/>
        </div>
      ))}
    </div>
  )
}
