import { useEffect, useState } from 'react'

export const useCheckBottom = () => {
  const [isBottom, setIsBottom] = useState(false)
  
  useEffect(() => {
    console.log('mount')
    window.addEventListener('scroll', windowScroll)
    
    return () => {
      console.log('unmount')
      window.removeEventListener('scroll', windowScroll)
    }
  }, [])

  const windowScroll = () => {
    const windowBottomPosition = window.scrollY + window.innerHeight
    const pageBottomPosition = document.getElementsByTagName('main')[0].offsetHeight

    console.log(windowBottomPosition, pageBottomPosition)
    console.log(windowBottomPosition === pageBottomPosition)

    if (windowBottomPosition === pageBottomPosition) {
      console.log('bottom')
      setIsBottom(true)
    } else {
      console.log('not bottom')
      setIsBottom(false)
    }
  }
  
  return isBottom
}
